/* eslint-disable no-unused-vars */
/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState } from 'react';
import song from './song.mp3';
function Music() {
    
    const [cond, setcond] = useState("loading");
    const [time, settime] = useState(-1);
    var audio = new Audio(song);
    // useEffect(()=>{
    //     setTimeout(() => {
    //         settime(Math.floor(audio.currentTime));
    //         console.log(audio.currentTime);
    //     }, 5000);
    // },[])
    audio.oncanplaythrough = function () {
        setcond("loaded");
        
        
    }
    audio.onloadStart=function(){
        setcond("loading");
    }
    
    
    audio.onended = function () {
        alert('ended');
    }
    audio.ontimeupdate=(()=>{
        //settime(Math.floor(audio.currentTime));
        //document.getElementsByTagName("p").style.ba=//;
        document.getElementById("pr").innerText=`${Math.floor(audio.currentTime)}s`;

    })

    

    return (
        <>
        <button onClick={(event)=>{audio.play();}}>Play</button>
        <button onClick={()=>{audio.pause();}}>Pause</button>            
    <p id="pr" style={{backgroundColor:"black",height:"50px",width:"100px",color:"white"}}>hello</p>
    <p>{cond} : {audio.duration}</p>
        </>
    )
}
export default Music;
